import React from 'react';
import './App.css';
import Die from './componenet/Die';

function App() {

  function allNewDice() {
    const newDice = [];
    for(let i=0; i<10; i++) {
      newDice.push(Math.ceil(Math.random() * 6));
    }
    return newDice;
  }

  console.log(allNewDice());

  return (
  <main>
    <div className="app">
      <div className="outer-box">
        <div className="inside-box">
          <h3>Tenzies</h3>
          <p>Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
          <div className='numbers'>
            <Die value='1' />
            <Die value='2' />
            <Die value='3' />
            <Die value='4' />
            <Die value='5' />
            <Die value='6' />
            <Die value='7' />
            <Die value='8' />
            <Die value='9' />
            <Die value='9' />
          </div>
          <div className='roll-btn'>Roll</div>
        </div>
      </div>
    </div>
  </main>
  );
}

export default App;
